    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
  }

  .metadata {
    margin: 10px 0;
  }

  p {
    font-size: 13px;
    margin: 0 0 6px 0;
  }
  p:first-of-type {
    border-top: 2px solid black;
    padding-top: 20px;
    margin-top: 20px;
  }

  /* print details */
  .cover-page {
    display: none;
    font-family: Georgia, 'Times New Roman', Times, serif;
    font-variant-numeric: lining-nums;
    font-size: 16px;
    line-height: 1;
    margin: 0;
    flex-direction: column;
    justify-content: space-between;
    align-items: flex-start;
    height: 10in;
    box-sizing: border-box;
  }

  /* footer */
  .footer {
    border-top: 2px solid black;
    border-bottom: 2px solid black;
    width: 100%;
    height: fit-content;
    padding: 0px;
    box-sizing: border-box;
    display: flex;
    flex-direction: column;
    justify-content: center;
    gap: 12px;
    padding: 14px 0;
  }
  .footer > svg {
    height: 12px;
